import engine.AITask
import org.apache.spark.ml.clustering.KMeans
import org.apache.spark.ml.linalg.SparseVector
import org.apache.spark.sql.SparkSession
import java.time.LocalDateTime
import java.time.format.DateTimeFormatter

new AITask {
  override def run(payload: String): Unit = {
    try {
      setLocalData(s"${getLocalData.mkString}\n${LocalDateTime.now.format(DateTimeFormatter.ofPattern("YYYYMMddHHmmss"))} 1:$payload")
      val spark = SparkSession.builder.master("local").appName(node).getOrCreate()
      val dataset = spark.read.format("libsvm").load(localDataName)
      val kmeans = new KMeans().setK(3).setSeed(1L)
      val model = kmeans.fit(dataset)
      val predictions = model.transform(dataset)
      val c = model.clusterCenters.map(v => v.apply(0))
      val sc = c.sorted
      val color = predictions.collect().filter(r => r.getAs[SparseVector](1).apply(0) == payload.toDouble).map(r => sc.indexOf(c.apply(r.getInt(2))) match {
        case 0 => "green"
        case 1 => "yellow"
        case 2 => "red"
      }).apply(0)
      postOutput(s"""["$payload $color"]""")
      postStatus(s""" { "shape": "dot", "fill": "$color", "text": "$payload"}""")
    }
    catch {
      case e:Exception => println(s"Exception: ${e.getMessage}")
    }
  }
}
